export function Contacts() {
    const [showPopup, setShowPopup] = useState(false);

    const togglePopup = () => {
        setShowPopup(!showPopup);
    };

    const closePopup = () => {
        setShowPopup(false);
    };

    return (
        <div className="onas-page" style={{ position: "relative" }}>
            <h1>Для связи с нами:</h1>
            <p
                style={{ color: "blue", cursor: "pointer" }}
                onClick={togglePopup}
            >
                +7 999 595 19 30
            </p>

            {showPopup && (
                <div
                >
                    <a
                        href="tel:+79995951930"
                    >
                        Позвонить
                    </a>
                    <a
                        href="https://t.me/maryashkiinss" 
                        target="_blank"
                        rel="noopener noreferrer"
                    >
                        Перейти в Telegram
                    </a>
                    <button
                        onClick={closePopup}
                    >
                        Закрыть
                    </button>
                </div>
            )}
        </div>
    );
}
