import React, { useState } from "react";

export function Contacts() {
    const [showPopup, setShowPopup] = useState(false);

    const togglePopup = () => {
        setShowPopup(!showPopup);
    };

    const closePopup = () => {
        setShowPopup(false);
    };

    return (
        <div className="onas-page" style={{ position: "relative" }}>
            <h1>Для связи с нами:</h1>
            <p
                style={{ color: "blue", cursor: "pointer" }}
                onClick={togglePopup}
            >
                +7 999 595 19 30
            </p>

            {showPopup && (
                <div
                    style={{
                        position: "absolute",
                        background: "white",
                        border: "1px solid #ccc",
                        borderRadius: "5px",
                        boxShadow: "0 4px 8px rgba(0, 0, 0, 0.1)",
                        padding: "10px",
                        top: "50px", // можно подстроить под дизайн
                        left: "20px",
                    }}
                >
                    <a
                        href="tel:+79995951930"
                        style={{
                            display: "block",
                            textDecoration: "none",
                            color: "#333",
                            marginBottom: "5px",
                        }}
                    >
                        Позвонить
                    </a>
                    <a
                        href="https://t.me/username" // Замените username на ваш ID в Telegram
                        target="_blank"
                        rel="noopener noreferrer"
                        style={{
                            display: "block",
                            textDecoration: "none",
                            color: "#333",
                        }}
                    >
                        Перейти в Telegram
                    </a>
                    <button
                        onClick={closePopup}
                        style={{
                            marginTop: "10px",
                            padding: "5px 10px",
                            backgroundColor: "#f5f5f5",
                            border: "1px solid #ccc",
                            borderRadius: "3px",
                            cursor: "pointer",
                        }}
                    >
                        Закрыть
                    </button>
                </div>
            )}
        </div>
    );
}
